// Captura a saída do nó IA Agent
const outputIA = $json["output"] || "";

// Normaliza a string para evitar problemas com formatações diferentes
const normalizedText = outputIA
    .replace(/### /g, "")  // Remove títulos Markdown como "### Pontuação"
    .replace(/- \*\*/g, "") // Remove prefixos como "- **Pontuação:**"
    .replace(/\*\*:/g, ":") // Remove "**: " do título para torná-lo mais legível
    .replace(/\*\*/g, "");  // Remove negrito restante para evitar confusão

// Função para encontrar o conteúdo de uma seção baseado em um título
function extractSection(text, startMarker, endMarker) {
    const startIndex = text.indexOf(startMarker);
    if (startIndex === -1) return "N/A";  // Retorna N/A se a seção não existir

    let endIndex = text.indexOf(endMarker, startIndex + startMarker.length);
    if (endIndex === -1) endIndex = text.length; // Se não encontrar o fim, vai até o final

    return text.substring(startIndex + startMarker.length, endIndex).trim();
}

// Extrai os blocos de informação
const pontuacao = extractSection(normalizedText, "Pontuação:", "\n");
const pontosFortes = extractSection(normalizedText, "Pontos Fortes:", "Pontos Fracos:");
const pontosFracos = extractSection(normalizedText, "Pontos Fracos:", "Sugestões para o Candidato:");
const sugestoes = extractSection(normalizedText, "Sugestões para o Candidato:", "Essa pontuação");

// Função para remover formatação Markdown e adicionar quebras de linha
function formatText(text) {
    return text
        .replace(/\n\s*\d+\.\s*/g, "\n") // Remove numeração automática (1., 2., 3.)
        .replace(/\n/g, "\n") // Mantém as quebras de linha
        .trim();
}

// Retorna os dados formatados corretamente
return [
  {
    json: {
      "Pontuação da IA": pontuacao,
      "Pontos Fortes": formatText(pontosFortes),
      "Pontos Fracos": formatText(pontosFracos),
      "Sugestões": formatText(sugestoes)
    }
  }
];

