// Recebe o array com a string de saída do nó de Agente de IA
const input = items[0].json.output;

// Divide a string em seções de Experiências Profissionais e Formação e Capacitação
const [experiencias, formacao] = input.split('\n\n');

// Processa as Experiências Profissionais
const experienciasArray = experiencias
  .replace('**Experiências Profissionais:**\n', '')
  .split('\n')
  .map(item => {
    const [empresa, cargo, periodo, responsabilidades] = item.split(' | ');
    return `
      <tr>
        <td>${empresa}</td>
        <td>${cargo}</td>
        <td>${periodo}</td>
        <td>${responsabilidades}</td>
      </tr>
    `;
  })
  .join('');

// Processa a Formação e Capacitação
const formacaoArray = formacao
  .replace('**Formação e Capacitação:**\n', '')
  .split('\n')
  .map(item => {
    const [curso, nivel, duracao] = item.split(' | ');
    return `
      <tr>
        <td>${curso}</td>
        <td>${nivel}</td>
        <td>${duracao}</td>
      </tr>
    `;
  })
  .join('');

// Cria o HTML final (sem as tags <html>, <body> e <style>)
const outputHtml = `
    <h2>Experiências Profissionais</h2>
    <table>
      <tr>
        <th>Empresa</th>
        <th>Cargo</th>
        <th>Período</th>
        <th>Responsabilidades</th>
      </tr>
      ${experienciasArray}
    </table>

    <h2>Formação e Capacitação</h2>
    <table>
      <tr>
        <th>Curso</th>
        <th>Nível</th>
        <th>Duração</th>
      </tr>
      ${formacaoArray}
    </table>
`;

// Retorna o HTML
return [{
  json: {
    output: outputHtml
  }
}];
