// Obtem todos os posts retornados pelo nó anterior ao "Recupera URLs das imagens"
const posts = $('Testa se há posts publicados').all();

// Processa os posts para consolidar os dados
return posts.map(post => {
  const featuredMediaId = post.json.featured_media; // ID da imagem destacada do post

  // Busca a imagem correspondente pelo ID no nó "Recupera URLs das imagens"
  const imageData = $('Recupera URLs das imagens').all().find(img => img.json.body.id === featuredMediaId);
  const imageUrl = imageData ? imageData.json.body.guid.rendered : 'URL_IMAGEM_PADRAO'; // Define a URL da imagem ou uma padrão

  // Formata a data e o horário no padrão dd/mm/yyyy hh:mm
  const formattedDateTime = post.json.date
    ? new Date(post.json.date).toLocaleDateString("pt-BR", {
        day: "2-digit",
        month: "2-digit",
        year: "numeric"
      }) +
      " " +
      new Date(post.json.date).toLocaleTimeString("pt-BR", {
        hour: "2-digit",
        minute: "2-digit"
      })
    : "Data e horário não disponíveis";

  // Retorna os dados consolidados
  return {
    id: post.json.id,
    title: post.json.title?.rendered || "Título não disponível",
    link: post.json.link,
    excerpt: (post.json.excerpt?.rendered || "Resumo não disponível").substring(0, 150) + "...",
    date: formattedDateTime, // Usa a data e o horário formatados
    imageUrl: imageUrl
  };
});

return [
    {
        json: {
            posts: $input.all()
                .map(item => `- ${item.json.excerpt.rendered}`) // Garante que o título seja extraído
                .join('\n') // Junta os títulos com quebras de linha
        }
    }
];
