    if (cleaned.length === 0) return 'Não Informado';
    if (cleaned.length !== 11) return 'Inválido';

    // Validação básica de CPF
    const isRepeated = /^(\d)\1+$/.test(cleaned);
    if (isRepeated) return 'Inválido';

    const calcCheckDigit = (digits, factor) => {
      let sum = 0;
      for (let i = 0; i < digits.length; i++) {
        sum += parseInt(digits[i]) * factor--;
      }
      const result = (sum * 10) % 11;
      return result === 10 ? 0 : result;
    };

    const digit1 = calcCheckDigit(cleaned.slice(0, 9), 10);
    const digit2 = calcCheckDigit(cleaned.slice(0, 10), 11);

    if (digit1 !== parseInt(cleaned[9]) || digit2 !== parseInt(cleaned[10])) {
      return 'Inválido';
    }

    // Formatar como 999.999.999-99
    return cleaned.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  })()
}}

