const partes = $input.first().json['Nome do(a) Colaborador(a)'].trim().split(/\s+/);

// Função para remover acentos e caracteres especiais
const normalizarTexto = (texto) => {
  return texto
    .normalize('NFD')                 // Separa os acentos das letras
    .replace(/[\u0300-\u036f]/g, '')  // Remove os sinais de acento
    .replace(/[^a-zA-Z0-9\s]/g, '');  // Remove outros caracteres especiais (ex: cedilha, pontuação)
};

const givenName = normalizarTexto(partes[0]);
const familyName = normalizarTexto(partes.slice(1).join(' '));
const ultimoNome = normalizarTexto(partes[partes.length - 1]);

// Geração de senha aleatória com 8 caracteres incluindo letras, números e caracteres especiais
const gerarSenha = () => {
  const letrasMaiusculas = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
  const letrasMinusculas = 'abcdefghijklmnopqrstuvwxyz';
  const numeros = '0123456789';
  const especiais = '!@#$%^&*()_+[]{}|;:,.<>?';

  const todas = letrasMaiusculas + letrasMinusculas + numeros + especiais;

  let senha = '';
  senha += letrasMaiusculas.charAt(Math.floor(Math.random() * letrasMaiusculas.length));
  senha += letrasMinusculas.charAt(Math.floor(Math.random() * letrasMinusculas.length));
  senha += numeros.charAt(Math.floor(Math.random() * numeros.length));
  senha += especiais.charAt(Math.floor(Math.random() * especiais.length));

  for (let i = senha.length; i < 8; i++) {
    senha += todas.charAt(Math.floor(Math.random() * todas.length));
  }

  return senha.split('').sort(() => 0.5 - Math.random()).join('');
};

const senha = gerarSenha();

const username = `${givenName.toLowerCase()}.${ultimoNome.toLowerCase()}`; // usado para gerar o email no formato esperado

const anotacoes = `
CPF: ${$json.CPF}
RG: ${$json.RG}
Endereço: ${$json["Endereço"]}
Telefone: ${$json.Telefone}
E-mail pessoal: ${$json["E-mail Particular"]}
`.trim();

return [
  {
    json: {
      username,
      givenName,
      familyName,
      password: senha,
      notes: anotacoes,
      Telefone: $json.Telefone,
      Endereço: $json["Endereço"]
    }
  }
];
