// Função para limpar a formatação Markdown básica
function cleanMarkdown(text) {
  if (!text || typeof text !== "string") return "";
  return text
    .replace(/\*\*(.*?)\*\*/g, "$1")    // Remove **negrito**
    .replace(/\*(.*?)\*/g, "$1")          // Remove *itálico*
    .replace(/###\s*(.*)/g, "\n$1")       // Ajusta títulos nível 3
    .replace(/##\s*(.*)/g, "\n$1")        // Ajusta títulos nível 2
    .replace(/#\s*(.*)/g, "\n$1")         // Ajusta títulos nível 1
    .trim();
}

// Função que converte o texto (com quebras de linha) em HTML,
// identificando itens de lista e seus subitens por meio da indentação.
// Linhas que não forem itens de lista são encapsuladas em parágrafos.
function convertMarkdownListsToHtmlNested(text) {
  const lines = text.split(/\r?\n/);
  let result = "";
  // Stack para controlar os níveis aninhados; cada item: { indent: number, type: "ul" ou "ol" }
  const stack = [];

  // Função auxiliar para fechar listas abertas até um determinado nível
  const closeListsUntil = (targetIndent) => {
    while (stack.length > 0 && stack[stack.length - 1].indent > targetIndent) {
      const popped = stack.pop();
      result += `</${popped.type}>\n`;
    }
  };

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    if (line.trim() === "") {
      // Linha em branco: fecha quaisquer listas abertas e insere uma quebra
      closeListsUntil(0);
      result += "\n";
      continue;
    }

    // Verifica se a linha é um item de lista não ordenada (usa "-" ou "•")
    const unorderedMatch = line.match(/^(\s*)[-•]\s+(.*)/);
    // Verifica se a linha é um item de lista ordenada (usa dígito seguido de ".")
    const orderedMatch = line.match(/^(\s*)(\d+)\.\s+(.*)/);

    // Se for um item e o conteúdo (após o marcador) estiver vazio, ignora-o
    if (unorderedMatch && unorderedMatch[2].trim() === "") continue;
    if (orderedMatch && orderedMatch[3].trim() === "") continue;

    if (unorderedMatch || orderedMatch) {
      // Define dados comuns
      let indent, content, listType;
      if (unorderedMatch) {
        indent = unorderedMatch[1].length;
        content = unorderedMatch[2];
        listType = "ul";
      } else {
        indent = orderedMatch[1].length;
        content = orderedMatch[3];
        listType = "ol";
      }

      // Se a pilha estiver vazia, inicia uma nova lista
      if (stack.length === 0) {
        result += `<${listType}>\n`;
        stack.push({ indent, type: listType });
      } else {
        const currentLevel = stack[stack.length - 1];
        if (indent > currentLevel.indent) {
          // Nível mais profundo: inicia uma nova lista aninhada
          result += `<${listType}>\n`;
          stack.push({ indent, type: listType });
        } else if (indent < currentLevel.indent) {
          // Volta para um nível superior: fecha as listas enquanto o nível for maior que o atual
          closeListsUntil(indent);
          // Se o tipo de lista for diferente do nível atual (ou se a pilha ficou vazia), inicia uma nova lista
          if (stack.length === 0 || stack[stack.length - 1].type !== listType) {
            result += `<${listType}>\n`;
            stack.push({ indent, type: listType });
          }
        } else {
          // Mesmo nível: se o tipo de lista mudar, fecha a anterior e abre a nova
          if (currentLevel.type !== listType) {
            stack.pop();
            result += `</${currentLevel.type}>\n`;
            result += `<${listType}>\n`;
            stack.push({ indent, type: listType });
          }
          // Caso contrário, permanece no mesmo nível
        }
      }
      // Adiciona o item da lista
      result += `<li>${content}</li>\n`;
    } else {
      // Linha que não é item de lista:
      // Fecha quaisquer listas em aberto antes de inserir um parágrafo
      closeListsUntil(0);
      result += `<p>${line.trim()}</p>\n`;
    }
  }

  // Fecha todas as listas que ainda estiverem abertas
  closeListsUntil(0);
  return result;
}

// --- Captura os textos dos nós de IA ---
const parecerCompletoRaw = $('Geração do Parecer Final e Pontuação').first().json.output || "";
const estimativaCustoRaw = $('Estimativa de Custo').first().json.output || "";
const avaliacaoRiscosRaw = $('Avaliação de Riscos do Projeto').first().json.output || "";
const alinhamentoPDIRaw = $('Análise de Adequação à Política de PDI').first().json.output || "";

// Aplica a limpeza do Markdown em cada texto
const parecerCompleto = cleanMarkdown(parecerCompletoRaw);
const estimativaCusto   = cleanMarkdown(estimativaCustoRaw);
const avaliacaoRiscos   = cleanMarkdown(avaliacaoRiscosRaw);
const alinhamentoPDI    = cleanMarkdown(alinhamentoPDIRaw);

// --- Extração da Nota Final ---
// Procura por "Nota Final", "Nota final (0-10)" ou "Nota" seguido de um valor numérico
const notaRegex = /(?:Nota Final|Nota final|Nota final \(0-10\)|Nota)\s*[:\-]?\s*(\d+)/i;
const notaMatch = parecerCompleto.match(notaRegex);
const notaFinal = notaMatch ? parseInt(notaMatch[1]) : null;
// Remove a parte referente à nota para evitar duplicidade
const parecerSemNota = parecerCompleto.replace(notaRegex, "").trim();

// --- Extração do Score de Custo ---
// Regex que captura variações como "Score de Custo", "Score de Custo do Projeto" ou "Score de custo (0-10):"
const scoreCustoRegex = /Score de Custo(?: do| de)?(?: Projeto)?\s*\(0-10\)\s*[:\-]?\s*(\d+)/i;
let scoreCustoMatch = 
  parecerCompleto.match(scoreCustoRegex) ||
  estimativaCusto.match(scoreCustoRegex) ||
  avaliacaoRiscos.match(scoreCustoRegex) ||
  alinhamentoPDI.match(scoreCustoRegex);
const scoreCusto = scoreCustoMatch ? parseInt(scoreCustoMatch[1]) : null;
// Remove a parte referente ao score dos textos
const parecerSemScore = parecerSemNota.replace(scoreCustoRegex, "").trim();
const estimativaCustoSemScore = estimativaCusto.replace(scoreCustoRegex, "").trim();

// --- Limpeza adicional: remove linhas que contenham apenas numeração (por exemplo, "3." ou "4.") ---
const parecerSemScoreClean = parecerSemScore.replace(/^\s*\d+\.\s*$/gm, "").trim();
const estimativaCustoClean = estimativaCustoSemScore.replace(/^\s*\d+\.\s*$/gm, "").trim();

// --- Converte os textos limpos para HTML com marcação de listas aninhadas ---
const alinhamentoPDI_html = convertMarkdownListsToHtmlNested(alinhamentoPDI || "Análise de alinhamento não encontrada.");
const avaliacaoRiscos_html = convertMarkdownListsToHtmlNested(avaliacaoRiscos || "Avaliação de riscos não encontrada.");
const estimativaCusto_html = convertMarkdownListsToHtmlNested(estimativaCustoClean || "Estimativa de custo não encontrada.");
const parecerFinal_html    = convertMarkdownListsToHtmlNested(parecerSemScoreClean || "Parecer não encontrado.");

// --- Retorna os valores organizados em variáveis separadas ---
return [{
    json: {
        "Alinhamento à Política de PDI": alinhamentoPDI_html,
        "Avaliação de Riscos": avaliacaoRiscos_html,
        "Estimativa de Custo": estimativaCusto_html,
        "Parecer Final": parecerFinal_html,
        "Nota": notaFinal !== null ? notaFinal : "Nota não encontrada.",
        "Score de Custo": scoreCusto !== null ? scoreCusto : "Score de custo não encontrado."
    }
}];

